import { useState } from 'react';
import './App.css';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';

function App() {
  const [endpoint, setEndpoint] = useState('');
  const [response, setResponse] = useState(null);

  const fetchTime = async () => {
    if (!endpoint) {
      alert('Please enter an endpoint');
      return;
    }

    try {
      const res = await fetch(endpoint);
      const data = await res.json();
      setResponse(data);
    } catch (error) {
      alert('Failed to fetch data. Check the endpoint.');
    }
  };

  return (
    <div className="App">
      <TextField
        label="API Endpoint"
        variant="outlined"
        fullWidth
        value={endpoint}
        onChange={(e) => setEndpoint(e.target.value)}
        style={{ margin: '0 auto', marginTop:"3rem", marginBottom:"3rem"}}
      />
      <Button variant='contained' color='success' onClick={fetchTime}>
        Fetch Time from Endpoint
      </Button>
      {response && (
        <div style={{ marginTop: '20px' }}>
          <p><strong>Hostname:</strong> {response.hostname}</p>
          <p><strong>Time:</strong> {response.time}</p>
        </div>
      )}
    </div>
  );
}

export default App;
